import logo from './logo.svg';
import './App.css';
import { useState, useEffect } from 'react';

function showDiv(toggle){
  document.getElementById(toggle).style.display = 'block';
  }

  function hideDiv(toggle){
  document.getElementById(toggle).style.display = 'none';
}

function App() {
  return (
    <div className="App">
      <h1>Olá seja bem-vindo a página de cadastro</h1>
      <label>Informe o nome do produto</label>
      <div>
        <input onClick={() => hideDiv('toggle')} type="text" placeholder="Digite o nome do produto" />
      </div>
      <br/><br/>
      <label>Informe o valor do produto</label>
      <div>
        <input type="number" placeholder="Digite o valor do produto" />
      </div>
      <br/><br/>
      <div>
			  <input type="checkbox" id="terms"> Produto segue os termos de uso </input>
      </div>
			<br/><br/>
			<label htmlFor="categorias">Escolha uma categoria:</label>
			<select className="categoria" name="categorias" id="categorias">
				<option value="doce">Doce</option>
				<option value="unha">Unha</option>
				<option value="cabelo">Cabelo</option>
				<option value="decoraçao">Decoraçao</option>
      </select>
			<br/><br/>
      <button onClick={() => showDiv('toggle')} >Cadastrar Produto</button>
      <br/><br/>
      <div id="toggle" style="display:none">Produto cadastrado com sucesso!</div>
    </div>
  );
}

export default App;
